// // // // // // // // // // // // // // // // // // // // // // // // // // // // // // // // //     {
// // // // // // // // // // // // // // // // // // // // // // // // // // // // // // // // //         int rows1 = matrix1.GetLength(0);
// // // // // // // // // // // // // // // // // // // // // // // // // // // // // // // // //         int cols1 = matrix1.GetLength(1);
// // // // // // // // // // // // // // // // // // // // // // // // // // // // // // // // //         int rows2 = matrix2.GetLength(0);
// // // // // // // // // // // // // // // // // // // // // // // // // // // // // // // // //         int cols2 = matrix2.GetLength(1);

// // // // // // // // // // // // // // // // // // // // // // // // // // // // // // // // //         if (cols1 != rows2)
// // // // // // // // // // // // // // // // // // // // // // // // // // // // // // // // //         {
