// // // // // // // // // // // // // // // // // // // // // // // // // // // // // // // // //         int[,] resultMatrix = new int[rows1, cols2];

// // // // // // // // // // // // // // // // // // // // // // // // // // // // // // // // //         for (int i = 0; i < rows1; i++)
// // // // // // // // // // // // // // // // // // // // // // // // // // // // // // // // //         {
