public class A
{
    protected int a = 10; // Change to protected so it's accessible in subclasses
}

public class B : A
{
    protected int b = 20; // Change to protected so it's accessible in subclasses
}

public class C : B
{
    protected int c;

    // Constructor to initialize c using a and b
    public C()
    {
        c = a + b;
    }

    // Display method to print the value of c
    public void Display()
    {
        Console.WriteLine("C is " + c);
    }
}

class Program
{
    public static void Main(string[] args)
    {
        C add = new C();
        add.Display();
    }
}